import {initialState} from './initialState';

/* selectors */
//export const getAllCart = ({cart}) => cart.products;
export const getAllCart = ({cart}) => cart.products.map(product=> product.data);

/* action name creator */
const reducerName = 'cart';
const createActionName = name => `app/${reducerName}/${name}`;

/* action types */
const ADD_TO_CART = createActionName('ADD_TO_CART');

/* action creators */
export const addToCart = payload => ({ payload, type: ADD_TO_CART });

/* reducer */
export const reducer = (statePart = initialState, action = {}) => {
  switch (action.type) {
    case ADD_TO_CART: {
      const some = statePart.products.filter(product => product.data === action.payload);
      if(some.length !== 0) {
        console.log(product.data.cartQuantiy);
      } else {
        return {
          ...statePart,
          products: [...statePart.products, {
            data: action.payload,
            productCounter: 0,
          }],
        };
      }
      console.log(statePart);
    }
    default:
      return statePart;
  }
};
