import {initialState} from './initialState';
import axios from 'axios';
import {API_URL} from '../config';

/* selectors */
export const getAllProducts = ({products}) => products;

/* action name creator */
const reducerName = 'products';
const createActionName = name => `app/${reducerName}/${name}`;

/* action types */
const FETCH_START = createActionName('FETCH_START');
const FETCH_SUCCESS = createActionName('FETCH_SUCCESS');
const FETCH_ERROR = createActionName('FETCH_ERROR');
const ADD_TO_CART = createActionName('ADD_TO_CART');

/* action creators */
export const fetchStarted = payload => ({ payload, type: FETCH_START });
export const fetchSuccess = payload => ({ payload, type: FETCH_SUCCESS });
export const fetchError = payload => ({ payload, type: FETCH_ERROR });
export const addToCart = payload => ({ payload, type: ADD_TO_CART });

/* thunk creators */
export const fetchProducts = () => {
  return async (dispatch) => {
    dispatch(fetchStarted());
    try {
      const res = await axios.get(`${API_URL}/products`);
      dispatch(fetchSuccess(res));
    } catch (err) {
      dispatch(fetchError(err.message || true));
    }
  };
};

/* reducer */
export const reducer = (statePart = initialState, action = {}) => {
  switch (action.type) {
    case FETCH_START: {
      return {
        ...statePart,
        loading: {
          active: true,
          error: false,
        },
      };
    }
    case FETCH_SUCCESS: {
      action.payload.data.forEach(product => product.cartQuantiy = 0);
      return {
        ...statePart,
        loading: {
          active: false,
          error: false,
        },
        data: action.payload,
      };
    }
    case FETCH_ERROR: {
      return {
        ...statePart,
        loading: {
          active: false,
          error: action.payload,
        },
      };
    }
    case ADD_TO_CART: {
      //console.log(statePart);
      const newStatePart = statePart.data.data.map(product => {
        if (product._id === action.payload._id) {
          product.cartQuantiy = product.cartQuantiy + 1;
          return product;
        } else {
          return product;
        }
      });
      localStorage.setItem('cart', JSON.stringify([...newStatePart]));
      return {
        data: {...newStatePart},
      };
    }
    default:
      return statePart;
  }
};
