import React, {useState, useContext, useEffect} from 'react';
import {useLocation} from 'react-router-dom';
import styles  from './User.module.scss';
import {Link} from 'react-router-dom';
import {MdKeyboardArrowRight} from 'react-icons/md/index';
import Login from '../Checkout/Login/Login';
import Register from '../Checkout/Register/Register';
import UserProfile from './UserProfile/UserProfile';
import {UserContext} from '../../../context/userContext';

const User = () => {
  const [isLogin, setIsLogin] = useState(true);
  const [isRegister, setIsRegister] = useState(false);
  const [currentUser] = useState(useContext(UserContext).user);
  const [activeToken, setActiveToken] = useState('');
  const location = useLocation();

  const activeLogin = () => {
    setIsLogin(true);
    setIsRegister(false);
  };

  const activeRegister = () => {
    setIsLogin(false);
    setIsRegister(true);
  };

  useEffect(()=> {
    const token = currentUser?.token;

    setActiveToken(token);
  },[location]);
  console.log(activeToken);
  return (
    <div className={styles.root}>
      <div className={styles.navigation}>
        <p>
          <Link to='/'>
            Home
          </Link>
          <MdKeyboardArrowRight />
          Login
        </p>
      </div>
      {!currentUser?.token &&
        <div className={styles.loginContainer}>
          <div className={styles.title}>
            {isLogin && <h1>Login</h1>}
            {isRegister && <h1>Register</h1>}
          </div>
          <div className={styles.data}>
            <div className={styles.dataType}>
              <p onClick={()=>activeLogin()} className={isLogin ? styles.active : ''}>
                Login
              </p>
              <p onClick={()=>activeRegister()} className={isRegister ? styles.active : ''}>
                Register
              </p>
            </div>
            <form>
              <div className={styles.form}>
                {isLogin && <Login />}
                {isRegister && <Register />}
              </div>
              {isLogin &&
              <button type='submit'>
                Login
              </button>
              }
              {isRegister &&
              <button type='submit'>
                Register
              </button>
              }
            </form>
          </div>
        </div>
      }
      {currentUser?.token && <UserProfile />}
    </div>
  );
};

export default User;
