import React, {useEffect} from 'react';
import {useSelector, useDispatch} from 'react-redux';
import {Link} from 'react-router-dom';
import Typing from 'react-typing-animation';
import styles from './Shop.module.scss';
import {getAllCategory, fetchCategory} from '../../../redux/categoryRedux';
import {getAllProducts, fetchProducts} from '../../../redux/productRedux';
import BannerShop from './BannerShop/BannerShop';
import FeaturedProduct from './FeaturedProduct/FeaturedProduct';

const Shop = () => {
  const categories = useSelector(getAllCategory);
  const products = useSelector(getAllProducts);
  const dispatch = useDispatch();

  useEffect(()=> {
    dispatch(fetchProducts());
    dispatch(fetchCategory());
  },[]);

  return (
    <div className={styles.root}>
      <div className={styles.subMenu}>
        <div className={styles.list}>
          {categories && categories.data.map((category,index) => {
            return (
              <Link key={index} to='/'>{category.name}</Link>
            );
          })}
        </div>
      </div>
      <BannerShop />
      <div className={styles.featuresProd}>
        <FeaturedProduct products={products}/>
      </div>
      <div className={styles.advert}>
        <div className={styles.titleCont}>
          <h1>Make your projects alive</h1>
          <Typing speed={180} startDelay={500} loop={true} hideCursor={true}>
            <p>Create.</p>
            <Typing.Delay ms={1000} />
            <Typing.Backspace count={8} />
            <Typing.Delay ms={1000} />
            <p>Share.</p>
            <Typing.Delay ms={1000} />
            <Typing.Backspace count={7} />
            <Typing.Delay ms={1000} />
            <p>Imagination
              <Typing.Delay ms={500} />
              <span>&nbsp;is your only limit.</span>
            </p>
            <Typing.Delay ms={1000} />
            <Typing.Backspace count={31} speed={30} />
            <Typing.Delay ms={1000} />
          </Typing>
        </div>
        <img src='/images/shop/robo3d.png' alt=""/>
      </div>
    </div>
  );
};

export default Shop;
