import React, {useState} from 'react';
import {useHistory} from 'react-router-dom';
import PropTypes from 'prop-types';
import styles from './ProductPage.module.scss';

const ProductPage = ({product}) => {
  const history = useHistory();
  const [currentProduct, setCurrentProduct] = useState(history.location.state);
  console.log(currentProduct);
  return (
    <div className={styles.root}>
      <div className={styles.container}>
        <div className={styles.img}>
          <img src={currentProduct.image} alt={currentProduct.name}/>
        </div>
        <div className={styles.data}>
          <h3>{currentProduct.name}</h3>
          <span className={styles.ratingShort}>
            <p>Read 1000 Reviews</p>
          </span>
          <p className={styles.desc}>{currentProduct.overview}</p>
          <p className={styles.stock}>Available in stock:
            <span> {currentProduct.quantity} </span>
            pcs.
          </p>
          <span className={styles.price}>{currentProduct.price} EUR</span>
          <form>
            <span>-</span>
            <input type="number"/>
            <span>+</span>
            <div>
              <button type='submit'>Add to cart</button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

ProductPage.propTypes = {
  product: PropTypes.array,
};

export default ProductPage;
