import React, {useState, useEffect} from 'react';
import {useSelector, useDispatch} from 'react-redux';
import styles from './Cart.module.scss';
import {Link} from 'react-router-dom';
import {MdKeyboardArrowRight} from 'react-icons/md/index';
import {BiMinus, BiCartAlt} from 'react-icons/bi/index';
import {BsPlus, BsArrowRightShort} from 'react-icons/bs';
import Button from '../../common/Button/Button';
import {getAllCart, handleAddQuantity} from '../../../redux/cartRedux';
import {getAllProducts} from '../../../redux/productRedux';

const Cart = () => {
  const [cartProducts, setCartProducts] = useState([]);
  const cart = useSelector(getAllProducts);
  const dispatch = useDispatch();
  console.log(cart);
  const cartItems = () => {
    let cartProduct = cart.length > 0 && cart.data.filter(cartProd => cartProd.cartQuantiy > 0) || [];
    setCartProducts(cartProduct);
  };

  const addQuantity = (id) => {
    dispatch(handleAddQuantity(id));
  };

  const minusQuantity = () => {

  };

  useEffect(()=> {
    cartItems();
  },[]);

  return (
    <div className={styles.root}>
      <div className={styles.navigation}>
        <p>
          <Link to='/'>
            Home
          </Link>
          <MdKeyboardArrowRight />
          Shoping Cart
        </p>
      </div>
      {cartProducts.length !== 0 &&
        <div className={styles.container}>
          <h1>Shopping Cart</h1>
          <div className={styles.productsContainer}>
            <table>
              <thead>
                <tr>
                  <td>image</td>
                  <td>product name</td>
                  <td>categoty</td>
                  <td>quantity</td>
                  <td>special discount</td>
                  <td>total</td>
                </tr>
              </thead>
              <tbody>
                {cartProducts && cartProducts.map((product, index) => {
                  return (
                    <tr key={index}>
                      <td><img src={product.image} alt={product.name}/></td>
                      <td className={styles.name}>{product.name}</td>
                      <td>{product.subCategoryId.name}</td>
                      <td className={styles.quantity}>
                        <div className={styles.quantityContainer}>
                          <div className={styles.inputContainer}>
                            <input
                              type="number"
                              id='quantity'
                              value={product.cartQuantiy}
                              defaultValue='1'
                            />
                            <div>
                              <span onClick={()=>addQuantity(product._id)}> <BsPlus/> </span>
                              <span onClick={minusQuantity}> <BiMinus/> </span>
                            </div>
                          </div>
                          <div className={styles.btnContainer}>
                            <button> X</button>
                          </div>
                        </div>
                      </td>
                      <td>special discount</td>
                      <td>€ {product.price}</td>
                    </tr>
                  );
                })}
              </tbody>
            </table>
          </div>
          <div className={styles.summarizing}>
            <div className={styles.containerSum}>
              <div className={styles.item}>
                <p>use coupon code</p>
                <span>
                  <button>
                    <BsArrowRightShort/>
                  </button>
                </span>
              </div>
              <div className={styles.final}>
                <div>
                  <p>Sub-Total</p>
                  <p>price</p>
                </div>
                <div>
                  <p>Discount Value</p>
                  <p>price</p>
                </div>
                <div>
                  <p>Total:</p>
                  <p>price</p>
                </div>
              </div>
              <div className={styles.btnContainer}>
                <Link to='/shop'>
                  <button>Continue Shopping</button>
                </Link>
                <Link to='/cart/checkout'>
                  <button>Checkout</button>
                </Link>
              </div>
            </div>
          </div>
        </div>
      }
      {cartProducts.length === 0 &&
        <div className={styles.containerEmpty}>
          <BiCartAlt />
          <div>
            <p>Your cart is currently empty.</p>
            <p>Back to shop and find something for you.</p>
            <div className={styles.btn}>
              <Button data='Go to shop' url='/shop'/>
            </div>
          </div>
        </div>
      }
    </div>
  );
};

export default Cart;
