import React from 'react';
import PropTypes from 'prop-types';
import styles from './Filters.module.scss';

const Filters = ({categories, products, subCategories}) => {
  const allCategories = categories.data;
  const allProducts = products.data;
  const allSubcategories = subCategories;

  const filterByCategory = (categoryName) => {


    const subCat = allCategories.filter(sub => sub.categoryId.name === categoryName);
    console.log(subCat);
    const filteredProducts = allProducts.filter(product => product?.subCategoryId?.name === categoryName);
  };

  return (
    <div className={styles.root}>
      <div className={styles.category}>
        <h3>Products</h3>
        {allCategories && allCategories.map((category, index) => {
          return (
            <p key={index} onClick={()=>filterByCategory(category.name)}>{category.name}</p>
          );
        })}
      </div>
      <div className={styles.sortBy}>
        <h3>Sort by:</h3>
        <p>Name: A-Z</p>
        <p>Name: Z-A</p>
        <p>Price: Low to High</p>
        <p>Price: High to Low</p>
      </div>
    </div>
  );
};

Filters.propTypes = {
  categories: PropTypes.object,
  products: PropTypes.oneOfType([
    PropTypes.array,
    PropTypes.object,
  ]),
  subCategories: PropTypes.oneOfType([
    PropTypes.array,
    PropTypes.object,
  ]),
};

export default Filters;
