import {initialState} from './initialState';

/* selectors */
//export const getAllCart = () => JSON.parse(localStorage.getItem('cart'));

/* action name creator */
const reducerName = 'cart';
const createActionName = name => `app/${reducerName}/${name}`;

/* action types */
const HANDLE_ADD_QUANTITY = createActionName('HANDLE_ADD_QUANTITY');

/* action creators */
export const handleAddQuantity = payload => ({ payload, type: HANDLE_ADD_QUANTITY });

/* reducer */
export const reducer = (statePart = initialState, action = {}) => {
  switch (action.type) {
    case HANDLE_ADD_QUANTITY: {
      console.log(statePart);
      const newStatePart = statePart.products.map(product => {
        if(product._id === action.payload) {
          console.log(product.cartQuantiy);
          product.cartQuantiy = product.cartQuantiy + 1;
          return product;
        } else {
          return product;
        }
      });
      console.log(newStatePart);
      localStorage.setItem('cart', JSON.stringify({newStatePart}));
    }
    default:
      return statePart;
  }
};
