          {/*    <legend>Personalia:</legend>*/}
          {/*    <input type="text"/>*/}
          {/*  </fieldset>*/}
          {/*  <div className={styles.btn}>*/}
          {/*    <p>cancel</p>*/}
          {/*    <button type='submit'>cos</button>*/}
          {/*  </div>*/}
          {/*</form>*/}
        </div>
      </div>
      </>
    );
};

