import React, {useState} from 'react';
import styles from './ProductPage.modules.scss';
import ProductMain from './ProductMain/ProductMain';
import FeaturedProduct from '../FeaturedProduct/FeaturedProduct';
import AdvertOne from './AdvertOne/AdvertOne';
import AdvertTwo from './AdvertTwo/AdvertTwo';
import Review from './Feedback/Review/Review';
import {useSelector} from 'react-redux';
import {getAllProducts} from '../../../../redux/productRedux';

const ProductPage = () => {
  const products = useSelector(getAllProducts);
  return (
    <div className={styles.root}>
      <div className={styles.productMain}>
        <ProductMain />
      </div>
      <div className={styles.advertOne}>
        <AdvertOne />
      </div>
      <div className={styles.featured}>
        <FeaturedProduct products={products} title='Related Products'/>
      </div>
      <div className={styles.review}>
        <Review />
      </div>
    </div>
  );
};

export default ProductPage;
