import React, {useState, useEffect, useContext} from 'react';
import styles  from './User.module.scss';
import {Link} from 'react-router-dom';
import {MdKeyboardArrowRight} from 'react-icons/md/index';
import Login from '../Checkout/Login/Login';
import Register from '../Checkout/Register/Register';
import {UserContext} from '../../../context/userContext';

const User = () => {
  const [isLogin, setIsLogin] = useState(true);
  const [isRegister, setIsRegister] = useState(false);
  const [currentUser, setCurrentUser] = useState({});

  const activeLogin = () => {
    setIsLogin(true);
    setIsRegister(false);
  };

  const activeRegister = () => {
    setIsLogin(false);
    setIsRegister(true);
  };

  useEffect(()=> {
    setCurrentUser(useContext(UserContext));
  },[]);

  return (
    <div className={styles.root}>
      <div className={styles.navigation}>
        <p>
          <Link to='/'>
            Home
          </Link>
          <MdKeyboardArrowRight />
          Login
        </p>
      </div>
      <div className={styles.title}>
        {isLogin && <h1>Login</h1>}
        {isRegister && <h1>Register</h1>}
      </div>
      <div className={styles.data}>
        <div className={styles.dataType}>
          <p onClick={()=>activeLogin()} className={isLogin ? styles.active : ''}>
            Login
          </p>
          <p onClick={()=>activeRegister()} className={isRegister ? styles.active : ''}>
            Register
          </p>
        </div>
        <form>
          <div className={styles.form}>
            {isLogin && <Login />}
            {isRegister && <Register />}
          </div>
          {isLogin &&
            <button type='submit'>
              Login
            </button>
          }
          {isRegister &&
          <button type='submit'>
            Register
          </button>
          }
        </form>
      </div>
    </div>
  );
};

export default User;
