import React from 'react';
import PropTypes from 'prop-types';
import styles from './Filters.module.scss';

const Filters = ({categories, products}) => {
  const allCategories = categories;
  const allProducts = products.data;

  const filterByCategory = (categoryName) => {
    console.log(allProducts);
    const filteredProducts = allProducts.filter(product => product.subCategory === categoryName);
    console.log(filteredProducts);
  };

  return (
    <div className={styles.root}>
      <div className={styles.category}>
        <h3>Products</h3>
        {allCategories && allCategories.data.map((category, index) => {
          return (
            <p key={index} onClick={()=>filterByCategory(category.name)}>{category.name}</p>
          );
        })}
      </div>
      <div className={styles.sortBy}>
        <h3>Sort by:</h3>
        <p>Name: A-Z</p>
        <p>Name: Z-A</p>
        <p>Price: Low to High</p>
        <p>Price: High to Low</p>
      </div>
    </div>
  );
};

Filters.propTypes = {
  categories: PropTypes.object,
  products: PropTypes.oneOfType([
    PropTypes.array,
    PropTypes.object,
  ]),
};

export default Filters;
