import React, {useContext, useEffect, useRef} from 'react';
import PropTypes from 'prop-types';
import styles from './Popup.module.scss';
import {AiOutlineClose} from 'react-icons/ai';
import {PopupContext} from '../../../context/popupContext';

const Popup = () => {
  const popup = useContext(PopupContext);
  const alertCont = useRef(null);
  const [initializer, setInitializer] = useState(false);


  const closePopup = () => {
    popup.closePopup();
  };

  const handleClickOutside = e => {
    if (alertCont.current && !alertCont.current.contains(e.target)) {
      close();
    }
  };
  const handlePressEsc = e => {
    if (e.keyCode === 27) {
      close();
    }
  };

  useEffect(()=> {
    document.addEventListener('mousedown', handleClickOutside);
    document.addEventListener('keydown', handlePressEsc);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
      document.removeEventListener('keydown', handlePressEsc);
    };
  },[close, initializer]);
  console.log(popup);
  return (
    <div className={styles.root}>
      <div className={styles.container}>
        <div className={styles.close} onClick={closePopup}>
          <AiOutlineClose />
        </div>
        <div className={styles.title}>{popup.popup.title}</div>
        {popup.popup.context}
