import {initialState} from './initialState';

/* selectors */
export const getAllCart = ({cart}) => JSON.parse(localStorage.getItem('cart'));

/* action name creator */
const reducerName = 'cart';
const createActionName = name => `app/${reducerName}/${name}`;

/* action types */
const HANDLE_ADD_QUANTITY = createActionName('HANDLE_ADD_QUANTITY');

/* action creators */
export const handleAddQuantity = payload => ({ payload, type: FETCH_START });

/* reducer */
export const reducer = (statePart = initialState, action = {}) => {
  switch (action.type) {
    default:
      return statePart;
  }
};
