import React, {useState, useEffect} from 'react';
import {useSelector} from 'react-redux';
import {getAllCart} from '../../../redux/cartRedux';
import PropTypes from 'prop-types';
import styles from './Cart.module.scss';
import {Link} from 'react-router-dom';
import {MdKeyboardArrowRight} from 'react-icons/md/index';
import {BsPlus} from 'react-icons/bs/index';
import {BiCartAlt, BiMinus} from 'react-icons/bi/index';

const Cart = () => {
  const getProductFromCart = useSelector(getAllCart);
  const [cartProducts, setCartProducts] = useState([]);

  useEffect(()=> {
    setCartProducts(getProductFromCart);
  },[]);
  console.log(cartProducts);
  return (
    <div className={styles.root}>
      <div className={styles.navigation}>
        <p>
          <Link to='/'>
            Home
          </Link>
          <MdKeyboardArrowRight />
          Shoping Cart
        </p>
      </div>
      <div className={styles.container}>
        <h1>Shopping Cart</h1>
        <div className={styles.productsContainer}>
          <table>
            <thead>
              <tr>
                <td>image</td>
                <td>product name</td>
                <td>categoty</td>
                <td>quantity</td>
                <td>special discount</td>
                <td>total</td>
              </tr>
            </thead>
            <tbody>
              {cartProducts && cartProducts.map((product,index) => {
                return (
                  <tr key={index}>
                    <td><img src={product.image} alt={product.name}/></td>
                    <td className={styles.name}>{product.name}</td>
                    <td>{product.subCategoryId.name}</td>
                    <td className={styles.quantity}>
                      <div className={styles.quantityContainer}>
                        <div className={styles.inputContainer}>
                          <input
                            type="number"
                            id='quantity'
                            defaultValue='1'
                          />
                          <div>
                            <span> <BsPlus /> </span>
                            <span> <BiMinus /> </span>
                          </div>
                        </div>
                        <div className={styles.btnContainer}>
                          <button> X </button>
                        </div>
                      </div>
                    </td>
                    <td>special discount</td>
                    <td>total</td>
                  </tr>
                );
              })}
            </tbody>
          </table>
        </div>
        <div className={styles.summarizing}>

        </div>
      </div>
    </div>
  );
};

Cart.propTypes = {

};

export default Cart;
