import React, {useState} from 'react';
import {useHistory} from 'react-router-dom';
import PropTypes from 'prop-types';
import styles from './ProductPage.module.scss';
import {BsPlus, BsArrowRightShort} from 'react-icons/bs';
import {BiMinus} from 'react-icons/bi';

const ProductPage = () => {
  const history = useHistory();
  const [currentProduct, setCurrentProduct] = useState(history.location.state);
  console.log(currentProduct);
  return (
    <div className={styles.root}>
      <div className={styles.container}>
        <div className={styles.general}>
          <span>Delivery
            <button>
              <BsArrowRightShort/>
            </button>
          </span>
          <div className={styles.deliveryDesc}>
            Our products are delivered all over Poland and each of EU Countries. If you need detail information which you can not find here, then please email info@ardurob.ar and we’ll provide you required information as soon as we can.
          </div>
          <span>Description
            <button>
              <BsArrowRightShort/>
            </button>
          </span>
        </div>
        <div className={styles.img}>
          <img src={currentProduct.image} alt={currentProduct.name}/>
        </div>
        <div className={styles.data}>
          <h3>{currentProduct.name}</h3>
          <span className={styles.ratingShort}>
            <p>Read 1000 Reviews</p>
          </span>
          <p className={styles.desc}>{currentProduct.overview}</p>
          <p className={styles.stock}>Available in stock:
            <span> {currentProduct.quantity} </span>
            pcs.
          </p>
          <span className={styles.price}>{currentProduct.price} EUR</span>
          <form>
            <p>Qty:</p>
            <span> <BiMinus /> </span>
            <input
              type="number"
              id='quantity'
              defaultValue='1'
            />
            <span> <BsPlus /> </span>
            <div>
              <button type='submit'>Add to cart</button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

ProductPage.propTypes = {

};

export default ProductPage;
