import {initialState} from './initialState';

/* selectors */
export const getAllCart = ({cart}) => cart.products;

/* action name creator */
const reducerName = 'cart';
const createActionName = name => `app/${reducerName}/${name}`;

/* action types */
const ADD_TO_CART = createActionName('ADD_TO_CART');

/* action creators */
export const addToCart = payload => ({ payload, type: ADD_TO_CART });

/* reducer */
export const reducer = (statePart = initialState, action = {}) => {
  switch (action.type) {
    case ADD_TO_CART: {
      const sameProduct = statePart.products.filter(product => product === action.payload);
      if(action.payload === sameProduct[0]) {
        let counter = 1;
        action.payload.counter = counter + 1;
        console.log(action.payload.counter);
      }

      return {
        ...statePart,
        products: [...statePart.products, action.payload],
      };
    }
    default:
      return statePart;
  }
};
