import React, {useState, useEffect, useRef} from 'react';
import PropTypes from 'prop-types';
import styles from './ProductList.module.scss';
import Filters from '../Filters/Filters';
import ProductBox from '../ProductBox/ProductBox';
import {Link} from 'react-router-dom';

const ProductList = ({products, categories}) => {
  const pageContainer = useRef([]);
  const [allProducts, setAllProducts] = useState([]);
  const [productsPerPage, setProductsPerPage] = useState([]);
  const [pageNumbers, setPageNumbers] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage, setItemsPerPage] = useState(8);

  /* ------- Render pages numbers ------- */
  const changePage = (e) => {
    setCurrentPage(e.target.id);
  };
  const pages = [];
  for(let i=1; i<= Math.ceil(allProducts.length/itemsPerPage);i++) {
    pages.push(i);
  }
  const renderPageNumbers = pages.map(number => {
    return (
      <li
        key={number}
        id={number}
        onClick={e => changePage(e)}
        className={currentPage === number ? styles.activePage : null}
      >
        {number}
      </li>
    );
  });
  /* ------------------------------------ */

  useEffect(()=> {
    if(products.data){
      if(products.data.data.length > 0) {
        products.data && setAllProducts(products.data.data);
        const indexOfLastItem = currentPage * itemsPerPage;
        const indexOfFirstItem = indexOfLastItem - itemsPerPage;
        const currentItems = products.data.data.slice(indexOfFirstItem, indexOfLastItem);
        products.data && setProductsPerPage(currentItems);
      }
    }
  },[products, currentPage]);



  console.log(pageContainer.current);
  return (
    <div className={styles.root}>
      <div className={styles.container}>
        <div className={styles.title}>
          <h1>All products</h1>
          <p>ArduRob products for great projects.</p>
        </div>
        <div className={styles.filters}>
          <Filters categories={categories} products={products}/>
        </div>
        <div className={styles.productList}>
          {productsPerPage && productsPerPage.map((product,index) => {
            return (
              <div
                key={index}
                className={styles.featureProductsContainerInner}
                ref={el => pageContainer.current[ind]}
              >
                <Link to='/' >
                  <ProductBox product={product}/>
                </Link>
              </div>
            );
          })}
          <div className={styles.pagesNumbers}>
            {renderPageNumbers}
          </div>
        </div>
      </div>
    </div>
  );
};

ProductList.propTypes = {
  categories: PropTypes.object,
  products: PropTypes.object,
};

export default ProductList;
